import React from 'react'

const Sales = () => {

    const [sales, setSales] = React.useState(null);



    React.useEffect(() => {
        getSales();
    }, [])
    console.log(sales);


    return (
        <section>
            <div className="banner">
                <h1>VENTAS</h1>
                {sales.length === 0 && <h1>No hay Ventas todavia ðŸ˜¢</h1>}
            </div>
            <div className="cart_section">
                <div className="cart_section_products">
                    {sales.map((item) => {
                        const { factura_id, fecha, cantidad, precio_unidad, product_id, subtotal, subtotal_producto, total_factura } = item
                        return (
                            <div key={factura_id} className="cartItem">
                                <div className="description">

                                    <h1>{product_id}</h1>
                                </div>


                                <div className="prices">
                                    <h2>{cantidad} x ${precio_unidad} = {precio_unidad * cantidad}</h2>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </div>
        </section>
    );
}

export default Sales
