import React from 'react'

const Sales = () => {

    const [sales, setSales] = React.useState(null);

    const getSales = async () => {
        const request = new XMLHttpRequest();
        request.open('get', 'http://localhost:5000/bills', true);


        request.onerror = function (err) {
            console.log(err);
        }
        request.onload = function () {
            if (request.status === 200) {
                const result = JSON.parse(request.responseText);
                setSales(result);
            }
        }
        request.send();
    };

    React.useEffect(() => {
        getSales();
    }, [])
    console.log(sales);


    return (
        <section>
            <div className="banner">
                <h1>VENTAS</h1>
                {sales.length === 0 && <h1>No hay Ventas todavia ðŸ˜¢</h1>}
            </div>
            <div className="cart_section">
                <div className="cart_section_products">
                    {sales.map((item) => {
                        const { factura_id, fecha, cantidad, precio_unidad, product_id, subtotal, subtotal_producto, total_factura } = item
                        return (
                            <div key={factura_id} className="cartItem">
                                <div className="description">
                                    <img src={img} alt={product_name} />
                                    <h1>{product_name}</h1>
                                </div>

                                <div className="buttons">
                                    <button onClick={() => removeProduct(item)} className="button_remove">-</button>
                                    <label>{cantidad}</label>
                                    <button onClick={() => addProduct(item)} className="button_add">+</button>
                                </div>
                                <div className="prices">
                                    <h2>{cantidad} x ${price * cantidad}</h2>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </div>
        </section>
    );
}

export default Sales
