import React from 'react'

const Bill = ({ factura, pedidos }) => {



    const { factura_id, fecha, subtotal, total_factura } = factura;
    const onlyfans = pedidos.filter((item) => item.factura_id === factura_id);
    console.log(onlyfans);
    console.log(pedidos);
    return (
        <div className="sales_section_products_active">
            <div key={factura_id} className="bill_active">
                <div className="description">
                    <h1>factura id</h1>
                    {onlyfans.map((product) => {
                        const { cantidad, precio_unidad, product_id, product_name, subtotal_producto } = product
                    })}
                    <h2>fecha</h2>
                    <h2>subtotal</h2>
                    <h2>Total factura:</h2>
                </div>

                <div className="prices">
                    <h1>{factura_id}</h1>
                    <h2>{fecha}</h2>
                    <h2>{subtotal}</h2>
                    <h2>{total_factura}</h2>
                </div>
            </div>
        </div>
    )
}

export default Bill
