import React from 'react'
import { AiOutlineShoppingCart } from 'react-icons/ai';
import { MdDone } from 'react-icons/md';
import { MdClear } from 'react-icons/md';



const Card = ({ data }) => {

    const [clicked, setCliked] = React.useState(false);

    const buyclass = () => {
        document.querySelector('.bottom').classList.add("clicked");
    }

    return (
        <>
            {data.map((card) => {

                const { id, product_name, img, price, product_description, stock } = card;

                return (
                    <div className="card" key={id}>
                        <div className="wrapper" >
                            <div className="container">
                                <div className="top">
                                    <img src={img} alt={product_name} />
                                </div>
                                <div className={clicked ? "bottom clicked" : "bottom"}>
                                    <div className="left">
                                        <div className="details">
                                            <h1>{product_name}</h1>
                                            <p>{price}</p>
                                        </div>
                                        <div onClick={() => setCliked(!clicked)} className="buy"><i className="material-icons"><AiOutlineShoppingCart /></i></div>
                                    </div>
                                    <div className="right">
                                        <div className="done"><i className="material-icons"><MdDone /></i></div>
                                        <div className="details">
                                            <p>Agregado al carrito</p>
                                        </div>
                                        <div onClick={() => setCliked(!clicked)} className="remove"><i className="material-icons"><MdClear /></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="inside">
                                {product_description && <div className="icon"><i className="material-icons">info</i></div>}
                                <div className="contents">
                                    <p>{product_description}</p>
                                    <h3>disponibles:{stock}</h3>

                                </div>
                            </div>
                        </div>
                    </div>
                );
            })}
        </>
    )
}

export default Card
